    function index()
    {
    }

    function getCachekey($prefix = '', $id = 0)
    {
        return "auth-ch-{$prefix}-{$id}";
    }

    /**
     *Desc:邮箱发送验证码
     * @return void
     *author:oszpac
     *date:2023-05-30
     */
    public function email_send_code()
    {
        $post = request()->post();
        $email = $post['email'];
        if (empty($email))
            ajaxmsg(lang('L0020'), 0);
        $mid = Db::name('member')
          ->where(['email' => $email])
          ->value('id');
        if (!$mid)
            ajaxmsg(lang('L0155'), 0);//邮箱不存在
        //生成随机六位数，不足六位两边补零
        $code = str_pad(mt_rand(0, 999999), 6, "0", STR_PAD_BOTH);
        $subject = lang('L0150');
        $body = '<H4><center>' . $subject . '</center></H4><H3><center style="color:#9373ef">' . $code . '</center></H3>';
        $emailer = new Email();
        $ret = $emailer->send($email, $subject, $body);
        if ($ret) {
            $key = $this->getCachekey('code', $mid);
            $value = md5($code);
            $expire = 300;
            setexredis($key, $expire, $value);
            ajaxmsg(lang('L0152'), 1);
        } else {
            ajaxmsg(lang('L0135'), 0);
        }
    }

    public function email_reset_psw()
    {
        $post = request()->post();
        $email = $post['email'];
        $code = $post['code'];
        $password = $post['password'];
        $check_password = $post['check_password'];
        if (empty($email) || empty($code) || empty($password) || empty($check_password))
            ajaxmsg(lang('L0020'), 0);
        if ($password !== $check_password)
            ajaxmsg(lang('L0068'), 0);
        $mid = Db::name('member')
          ->where(['email' => $email])
          ->value('id');
        if (!$mid)
            ajaxmsg(lang('L0155'), 0);//邮箱不存在
        $key = $this->getCachekey('code', $mid);
        $md5_code = getredis($key);
        if (md5($code) != $md5_code)
            ajaxmsg(lang('L0153'), 0);
        $newpwd = Hash::make((string)$password);
        $newid = Db::name('member')
          ->where(["id" => $mid])->update(['passwd' => $newpwd]);
        if ($newid) {
            ajaxmsg(lang('L0126'), 1);
        } else ajaxmsg(lang('L0127'), 0);
    }

    //谷歌验证器修改密码
    function ggAuthSetPwd()
    {
        $post = request()->post();
        //邮箱或者手机号
        $mobile_email = $post['mobile_email'];
        // 谷歌身份验证器上展示的6位数字验证码
        $code = $post['code'];
        $password = $post['password'];
        $check_password = $post['check_password'];
        if (empty($mobile_email) || empty($code) || empty($password) || empty($check_password))
            ajaxmsg(lang('L0020'), 0);
        if ($password !== $check_password)
            ajaxmsg(lang('L0068'), 0);
        $where = [];
        if (is_numeric($mobile_email)) {
            $where['mobile'] = $mobile_email;
        } else {
            $where['email'] = $mobile_email;
        }
        $where['is_del'] = 0;
        $user = Db::name('member')
          ->where($where)
          ->field('id,google_auth,passwd')
          ->find();
        if (!$user)
            ajaxmsg('验证器未绑定', 0, '', true, ['msgCode' => 'L0158']);
        import('GoogleAuthenticator', EXTEND_PATH);
        $google = new \PHPGangsta_GoogleAuthenticator();
        $time = 1;//0表示实时 误差时间，谷歌身份验证器30秒更换一次，这个表示最大的误差时间
        // 获取验证结果 返回结果true，false
        $checkResult = $google->verifyCode($user['google_auth'], $code, $time);
        if (!$checkResult)
            ajaxmsg(lang('L0153'), 0);//验证码不正确
//        L0069
        $newpwd = Hash::make((string)$password);
        if ($newpwd == $user['passwd']) {
            ajaxmsg('新密码与老密码不能一样', 0, '', true, ['msgCode' => 'L0069']);
        }
        $newid = Db::name('member')
          ->where(["id" => $user['id']])->update(['passwd' => $newpwd]);
        if ($newid) {
            ajaxmsg('设置成功', 1, '', true, ['msgCode' => 'L0126']);
        } else ajaxmsg('修改失败', 0, '', true, ['msgCode' => 'L0127']);
    }

