    public function getHK_KLine()
    {
        $result = [];
        $codes = ['HSI', 'HSCCI', 'HSCEI'];
        foreach ($codes as $code) {
            $data_key = 'HK_KLine.' . $code;
            $expire_key = 'HK_KLine_expire.' . $code;
            $data = Cache::get($data_key);
            $expire = Cache::get($expire_key);
            if ($expire) {
                $result[$code] = $data;
            } else {
                $web_data = webHKKLine($code);
                if ($web_data['result']['data']) {
                    $result[$code] = $web_data['result']['data'];
                    Cache::set($data_key, $web_data['result']['data']);
                    Cache::set($expire_key, '1', 60);
                } else {
                    $result[$code] = $data;
                }
            }
        }
        ajaxmsg('获取成功', 1, $result, true, ['msgCode' => 'L0014']);
    }

